import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Link} from 'react-router'

import {fetchPokemons,loadMorePokemons} from 'actions'
import {getPokemons} from 'selectors'


class Pokemons extends Component {
  componentDidMount () {
    this.props.fetchPokemons()
  }

  renderPokemon (pokemon, index) {
   // const imgSrc = `./pokemons/${id}.png`;
    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>

        <img
            className='img-thumbnail'
            src={`/pokemonsImg/${pokemon.id}.png`}
            alt={pokemon.name}
          />
          <div className='caption'>
          <h4>
              <Link to={`/pokemons/${pokemon.id}`}>
                {pokemon.name}
              </Link>
            </h4>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
              >
                Catch Now!
              </button>
              <Link
                to={`/pokemons/${pokemon.id}`}
                className='btn btn-default'
              >
                More info
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }


  render () {
    const {pokemons, loadMorePokemons} = this.props
    return (
      <div>
        <div className='books row'>
          {pokemons.map((pokemon, index) => this.renderPokemon(pokemon, index))}
        </div>
        <div className='row'>
          <div className='col-md-12'>
            <button
              onClick={loadMorePokemons}
              className='pull-right btn btn-primary'
            >
              Load More
            </button>
          </div>
        </div>
      </div>

    )
  }
}

const mapStateToProps = state => {
  return {
    pokemons: getPokemons(state)
  }
}

const mapDispatchToProps = {
  fetchPokemons,
  loadMorePokemons
}

export default connect(mapStateToProps, mapDispatchToProps)(Pokemons)
