import R from 'ramda'

const getPokemonById = (state, id) => R.prop(id, state.pokemons)

export const getPokemons = state => {
  const pokemons = R.map(id => getPokemonById(state, id), state.pokemonsPage.ids)
  return pokemons
}

export const getRenderedPokemonsLength = state => R.length(state.pokemonsPage.ids)